export to /home/datareq/generic_etl/wip/active_job_detail_shiredel8.csv of del modified by coldel, datesiso select ua.login_id,rja.company_name,rja.ad_id,rja.job_title,ua.display_count as job_views, (select count(1) from site_inbox_response sir where sir.ad_id = rja.ad_id) as application_count, monthname(ua.posting_date), ua.posting_date  from user_ad ua, rcrt_job_ads rja where ua.LOGIN_ID = 'shiredel8' and ua.NET_STATUS = 11 and ua.POSTING_DATE <= current date and ua.EXPIRY_DATE >= current date and rja.ad_id = ua.ad_id order by rja.ad_id asc with ur
