MERGE INTO JOB_ALERT_PACEMAKER AS jap  USING (select rja.AD_ID, rja.SKILL, rja.LOCATION, rja.JOB_FUNCTION, char(rja.WORK_EXP) as MIN_EXP, char(rja.WORK_EXP2) as MAX_EXP from  RCRT_JOB_ADS rja, USER_AD ua where ua.AD_ID = rja.AD_ID and ua.SUB_CAT_ID = 2710 and ua.net_status = 11 and ua.LOGIN_ID in ('jkt_01') ) AS cmp  ON (jap.AD_ID = cmp.AD_ID)  WHEN MATCHED THEN    UPDATE SET jap.ad_id = cmp.AD_ID, jap.skill = cmp.SKILL, jap.loc_id = cmp.LOCATION, jap.function_id = cmp.JOB_FUNCTION,       jap.min_exp = cmp.MIN_EXP,       jap.max_exp =cmp.MAX_EXP,       jap.entry_date = CURRENT TIMESTAMP,       jap.job_process_status = int('1')  WHEN NOT MATCHED THEN    INSERT (ad_id,skill,loc_id,function_id,min_exp,max_exp,entry_date,job_process_status)    VALUES(cmp.AD_ID,cmp.SKILL,cmp.LOCATION,cmp.JOB_FUNCTION ,cmp.MIN_EXP,cmp.MAX_EXP,CURRENT TIMESTAMP,int('1'))

